import React, { useState, useRef, useEffect } from "react"
import { Link } from "react-router-dom"

function ForgotPasswordPage() {
  const [currentStep, setCurrentStep] = useState("request")
  const [email, setEmail] = useState("")
  const [emailError, setEmailError] = useState("")
  const [codeError, setCodeError] = useState("")
  const [passwordError, setPasswordError] = useState("")
  const [confirmError, setConfirmError] = useState("")
  const [verificationCode, setVerificationCode] = useState("")
  const [enteredCode, setEnteredCode] = useState(["", "", "", "", "", ""])
  const [newPassword, setNewPassword] = useState("")
  const [confirmPassword, setConfirmPassword] = useState("")
  const [passwordStrength, setPasswordStrength] = useState(0)
  const [showNewPassword, setShowNewPassword] = useState(false)
  const [showConfirmPassword, setShowConfirmPassword] = useState(false)
  const [loading, setLoading] = useState(false)
  const [resendDisabled, setResendDisabled] = useState(false)
  const [resendText, setResendText] = useState("Renvoyer")
  const codeInputRefs = useRef([])

  useEffect(() => {
    codeInputRefs.current = codeInputRefs.current.slice(0, 6)
  }, [])

  const validateEmail = (email) => {
    const re =
      /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
    return re.test(String(email).toLowerCase())
  }

  const generateCode = () => {
    return Math.floor(100000 + Math.random() * 900000).toString()
  }

  const evaluatePasswordStrength = (password) => {
    let strength = 0
    if (password.length >= 8) strength += 25
    if (password.match(/[A-Z]/)) strength += 25
    if (password.match(/[0-9]/)) strength += 25
    if (password.match(/[^A-Za-z0-9]/)) strength += 25
    return strength
  }

  const handleRequestSubmit = (e) => {
    e.preventDefault()
    setEmailError("")
    if (!validateEmail(email)) {
      setEmailError("Veuillez entrer une adresse email valide.")
      return
    }
    setLoading(true)
    setTimeout(() => {
      setLoading(false)
      const code = generateCode()
      setVerificationCode(code)
      // For demo: show code in console
      console.log("Code de v√©rification (√† des fins de d√©monstration):", code)
      setCurrentStep("verify")
      setEnteredCode(["", "", "", "", "", ""])
      setTimeout(() => {
        if (codeInputRefs.current[0]) {
          codeInputRefs.current[0].focus()
        }
      }, 100)
    }, 1500)
  }

  const handleVerifySubmit = (e) => {
    e.preventDefault()
    setCodeError("")
    const code = enteredCode.join("")
    setLoading(true)
    setTimeout(() => {
      setLoading(false)
      if (code === verificationCode) {
        setCurrentStep("reset")
      } else {
        setCodeError("Code incorrect. Veuillez r√©essayer.")
      }
    }, 1000)
  }

  const handleResetSubmit = (e) => {
    e.preventDefault()
    setPasswordError("")
    setConfirmError("")
    if (passwordStrength < 75) {
      setPasswordError("Le mot de passe n'est pas assez s√©curis√©.")
      return
    }
    if (newPassword !== confirmPassword) {
      setConfirmError("Les mots de passe ne correspondent pas.")
      return
    }
    setLoading(true)
    setTimeout(() => {
      setLoading(false)
      setCurrentStep("success")
    }, 1500)
  }

  const handleCodeChange = (index, value) => {
    if (!/^\d*$/.test(value)) return
    const newCode = [...enteredCode]
    newCode[index] = value
    setEnteredCode(newCode)
    if (value && index < 5) {
      codeInputRefs.current[index + 1].focus()
    }
  }

  const handleCodeKeyDown = (index, e) => {
    if (e.key === "Backspace") {
      if (!enteredCode[index] && index > 0) {
        codeInputRefs.current[index - 1].focus()
      }
    }
  }

  const handleCodePaste = (e) => {
    e.preventDefault()
    const pastedData = e.clipboardData.getData("text")
    const digits = pastedData.match(/\d/g)
    if (digits && digits.length > 0) {
      const newCode = [...enteredCode]
      for (let i = 0; i < Math.min(digits.length, 6); i++) {
        newCode[i] = digits[i]
      }
      setEnteredCode(newCode)
      const lastIndex = Math.min(digits.length, 6) - 1
      if (codeInputRefs.current[lastIndex]) {
        codeInputRefs.current[lastIndex].focus()
      }
    }
  }

  const handleResendCode = (e) => {
    e.preventDefault()
    if (resendDisabled) return
    setResendDisabled(true)
    const code = generateCode()
    setVerificationCode(code)
    console.log("Nouveau code de v√©rification:", code)
    setEnteredCode(["", "", "", "", "", ""])
    if (codeInputRefs.current[0]) {
      codeInputRefs.current[0].focus()
    }
    setResendText("Code envoy√©!")
    setTimeout(() => {
      setResendDisabled(false)
      setResendText("Renvoyer")
    }, 30000)
  }

  useEffect(() => {
    setPasswordStrength(evaluatePasswordStrength(newPassword))
  }, [newPassword])

  return (
    <main className="container">
      {/* √âtape 1: Demande de r√©initialisation */}
      <div className={`card ${currentStep !== "request" ? "hidden" : ""}`}>
        <div className="card-header">
          <div className="icon-container">
            {/* You can use an SVG or icon here */}
            <span role="img" aria-label="email">üìß</span>
          </div>
          <h1 className="card-title">Mot de passe oubli√©</h1>
          <p className="card-description">Entrez votre adresse email et nous vous enverrons un code de v√©rification.</p>
        </div>
        <div className="card-content">
          <form onSubmit={handleRequestSubmit}>
            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input
                type="email"
                id="email"
                name="email"
                placeholder="votre@email.com"
                required
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
              {emailError && <span className="error-message">{emailError}</span>}
            </div>
            <button type="submit" className="submit-button" disabled={loading}>
              {loading && <div className="spinner"></div>}
              <span className="button-text">Envoyer le code</span>
            </button>
          </form>
        </div>
        <div className="card-footer">
          <Link to="/login" className="link">
            Retour √† la connexion
          </Link>
        </div>
      </div>

      {/* √âtape 2: V√©rification du code */}
      <div className={`card ${currentStep !== "verify" ? "hidden" : ""}`}>
        <div className="card-header">
          <div className="icon-container">
            <span role="img" aria-label="phone">üì±</span>
          </div>
          <h1 className="card-title">V√©rification du code</h1>
          <p className="card-description">
            Nous avons envoy√© un code √† <span className="font-medium">{email}</span>. Veuillez entrer ce code ci-dessous.
          </p>
        </div>
        <div className="card-content">
          <form onSubmit={handleVerifySubmit}>
            <div className="form-group">
              <label htmlFor="verification-code">Code de v√©rification</label>
              <div className="code-input-container">
                {[0, 1, 2, 3, 4, 5].map((index) => (
                  <input
                    key={index}
                    type="text"
                    maxLength="1"
                    className="code-input"
                    required
                    value={enteredCode[index]}
                    onChange={(e) => handleCodeChange(index, e.target.value)}
                    onKeyDown={(e) => handleCodeKeyDown(index, e)}
                    onPaste={index === 0 ? handleCodePaste : undefined}
                    ref={(el) => (codeInputRefs.current[index] = el)}
                  />
                ))}
              </div>
              {codeError && <span className="error-message">{codeError}</span>}
            </div>
            <button type="submit" className="submit-button" disabled={loading || enteredCode.some((digit) => !digit)}>
              {loading && <div className="spinner"></div>}
              <span className="button-text">V√©rifier le code</span>
            </button>
          </form>
          <div className="resend-container">
            <p>
              Vous n'avez pas re√ßu de code?{" "}
              <a
                href="#"
                className="link"
                onClick={handleResendCode}
                style={{ opacity: resendDisabled ? 0.5 : 1, pointerEvents: resendDisabled ? "none" : "auto" }}
              >
                {resendText}
              </a>
            </p>
          </div>
        </div>
        <div className="card-footer">
          <button className="back-button" onClick={() => setCurrentStep("request")}>
            Retour
          </button>
        </div>
      </div>

      {/* √âtape 3: Nouveau mot de passe */}
      <div className={`card ${currentStep !== "reset" ? "hidden" : ""}`}>
        <div className="card-header">
          <div className="icon-container">
            <span role="img" aria-label="lock">üîí</span>
          </div>
          <h1 className="card-title">Nouveau mot de passe</h1>
          <p className="card-description">Veuillez cr√©er un nouveau mot de passe s√©curis√©.</p>
        </div>
        <div className="card-content">
          <form onSubmit={handleResetSubmit}>
            <div className="form-group">
              <label htmlFor="new-password">Nouveau mot de passe</label>
              <div className="password-input-container">
                <input
                  type={showNewPassword ? "text" : "password"}
                  id="new-password"
                  name="new-password"
                  required
                  value={newPassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                />
                <button type="button" className="toggle-password" onClick={() => setShowNewPassword(!showNewPassword)}>
                  {showNewPassword ? "üôà" : "üëÅÔ∏è"}
                </button>
              </div>
              {passwordError && <span className="error-message">{passwordError}</span>}
              <div className="password-strength-meter">
                <div
                  className="strength-bar"
                  style={{
                    width: `${passwordStrength}%`,
                    backgroundColor: passwordStrength < 50 ? "#e53e3e" : passwordStrength < 75 ? "#dd6b20" : "#38a169",
                  }}
                ></div>
              </div>
              <p className="password-hint">
                Le mot de passe doit contenir au moins 8 caract√®res, une majuscule, un chiffre et un caract√®re sp√©cial.
              </p>
            </div>
            <div className="form-group">
              <label htmlFor="confirm-password">Confirmer le mot de passe</label>
              <div className="password-input-container">
                <input
                  type={showConfirmPassword ? "text" : "password"}
                  id="confirm-password"
                  name="confirm-password"
                  required
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                />
                <button
                  type="button"
                  className="toggle-password"
                  onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                >
                  {showConfirmPassword ? "üôà" : "üëÅÔ∏è"}
                </button>
              </div>
              {confirmError && <span className="error-message">{confirmError}</span>}
            </div>
            <button type="submit" className="submit-button" disabled={loading}>
              {loading && <div className="spinner"></div>}
              <span className="button-text">R√©initialiser le mot de passe</span>
            </button>
          </form>
        </div>
        <div className="card-footer">
          <button className="back-button" onClick={() => setCurrentStep("verify")}>
            Retour
          </button>
        </div>
      </div>

      {/* √âtape 4: Confirmation finale */}
      <div className={`card ${currentStep !== "success" ? "hidden" : ""}`}>
        <div className="card-header">
          <div className="icon-container">
            <span role="img" aria-label="check">‚úÖ</span>
          </div>
          <h1 className="card-title">Mot de passe r√©initialis√©</h1>
          <p className="card-description">
            Votre mot de passe a √©t√© r√©initialis√© avec succ√®s. Vous pouvez maintenant vous connecter avec votre nouveau
            mot de passe.
          </p>
        </div>
        <div className="card-footer">
          <Link to="/login" className="submit-button">
            Se connecter
          </Link>
        </div>
      </div>
    </main>
  )
}

export default ForgotPasswordPage
