import React from "react"
import "../styles/PaymentForm.css"
function PaymentForm({ cardData, onInputChange, onInputFocus }) {
  return (
    <form>
      {/* Minimal payment form fields */}
      <input
        type="text"
        placeholder="Card Number"
        value={cardData.number}
        onChange={e => onInputChange("number", e.target.value)}
        onFocus={() => onInputFocus("number")}
      />
      <input
        type="text"
        placeholder="Name"
        value={cardData.name}
        onChange={e => onInputChange("name", e.target.value)}
        onFocus={() => onInputFocus("name")}
      />
      <input
        type="text"
        placeholder="MM/YY"
        value={cardData.expiry}
        onChange={e => onInputChange("expiry", e.target.value)}
        onFocus={() => onInputFocus("expiry")}
      />
      <input
        type="text"
        placeholder="CVC"
        value={cardData.cvc}
        onChange={e => onInputChange("cvc", e.target.value)}
        onFocus={() => onInputFocus("cvc")}
      />
    </form>
  )
}

export default PaymentForm
